	/**
	 * Return Whether shop is open or not
	 *
	 * @return mixed|void
	 */
	function is_open() {

		$woc_is_open     = false;
		$current_time    = current_datetime();
		$today_schedules = $this->todays_schedule_details();

		foreach ( Utils::get_args_option( 'schedules', $today_schedules, array() ) as $schedule_id => $schedule ) {

			$open_time  = DateTime::createFromFormat( 'h:i a', Utils::get_args_option( 'open', $schedule ), wp_timezone() );
			$close_time = DateTime::createFromFormat( 'h:i a', Utils::get_args_option( 'close', $schedule ), wp_timezone() );
			if ( $current_time >= $open_time && $current_time <= $close_time ) {
				$woc_is_open = true;
				break;
			}
		}

		return apply_filters( 'WOOOPENCLOSE/Filters/is_open', $woc_is_open );
	}


	/**
	 * Return TimeZone String
	 *
	 * @return false|mixed|string|void
	 */
	function get_timezone_string() {

		// if site timezone string exists, return it
		if ( $timezone = get_option( 'timezone_string' ) ) {
			return $timezone;
		}

		// get UTC offset, if it isn't set then return UTC
		if ( 0 === ( $utc_offset = get_option( 'gmt_offset', 0 ) ) ) {
			return 'UTC';
		}

		// adjust UTC offset from hours to seconds
		$utc_offset *= 3600;

		// attempt to guess the timezone string from the UTC offset
		if ( $timezone = timezone_name_from_abbr( '', $utc_offset, 0 ) ) {
			return $timezone;
		}

		// last try, guess timezone string manually
		$is_dst = date( 'I' );

		foreach ( timezone_abbreviations_list() as $abbr ) {
			foreach ( $abbr as $city ) {
				if ( $city['dst'] == $is_dst && $city['offset'] == $utc_offset ) {
					return $city['timezone_id'];
				}
			}
		}

		// fallback to UTC
		return 'UTC';
	}


	/**
	 * Return Today's Schedules Details
	 *
	 * @return array|mixed
	 */
	function todays_schedule_details( $return = 'all' ) {

		if ( empty( $this->get_active_schedule_id() ) ) {
			return array();
		}

		$wooopenclose_data = Utils::get_meta( 'wooopenclose_data', $this->get_active_schedule_id() );
		$today_id          = $this->get_current_day_id();
		$schedule_details  = array(
			'default_label' => Utils::get_args_option( $today_id . '_default_label', $wooopenclose_data, 0 ),
			'label'         => Utils::get_args_option( $today_id . '_label', $wooopenclose_data ),
			'schedules'     => Utils::get_args_option( $today_id . '_schedules', $wooopenclose_data ),
		);

		if ( isset( $schedule_details[ $return ] ) && 'all' !== $return ) {
			$schedule_details = Utils::get_args_option( $return, $schedule_details );
		}

		return apply_filters( 'WOOOPENCLOSE/Filters/todays_schedule_details', $schedule_details );
	}


	function current_schedule_details() {

		$schedules        = $this->todays_schedule_details( 'schedules' );
		$current_day      = $this->get_day_name();
		$current_time     = wp_date( 'U' );
		$schedule_details = array();

		foreach ( $schedules as $schedule ) {

			if ( ! empty( $schedule_details ) ) {
				continue;
			}

			$this_time_open  = Utils::get_args_option( 'open', $schedule );
			$this_time_close = Utils::get_args_option( 'close', $schedule );
			$date_time_open  = new DateTime( $current_day . ' ' . $this_time_open, wp_timezone() );
			$date_time_close = new DateTime( $current_day . ' ' . $this_time_close, wp_timezone() );

			if ( $current_time > $date_time_open->getTimestamp() && $current_time < $date_time_close->getTimestamp() ) {
				$schedule_details = $schedule;
			}
		}

		return $schedule_details;
	}


	/**
	 * Return Current Day ID
	 *
	 * @return int
	 */
	function get_current_day_id() {

		switch ( strtolower( current_time( 'D' ) ) ) {
			case 'sat' :
				return 10001;
			case 'sun' :
				return 10002;
			case 'mon' :
				return 10003;
			case 'tue' :
				return 10004;
			case 'wed' :
				return 10005;
			case 'thu' :
				return 10006;
			case 'fri' :
				return 10007;
		}

		return 10001;
	}


