	/**
	 * Return Next Opening Time
	 *
	 * @param string $time_for open | close | toggle
	 * @param string $format
	 *
	 * @return mixed|void
	 */
	function get_next_time( $time_for = '', $format = 'M j Y G:i:s', $return_utc = false ) {

		// get today's time
		$times = $this->calculate_times( $this->todays_schedule_details( 'schedules' ), $time_for );

		// today is blank
		if ( empty( $times ) ) {

			$current_day        = $this->get_current_day_id() + 1;
			$current_day_index  = array_search( $current_day, array_keys( $this->get_all_schedules() ) );
			$after_current_day  = array_slice( $this->get_all_schedules(), $current_day_index, null, true );
			$before_current_day = array_diff_recursive( $this->get_all_schedules(), $after_current_day );
			$new_schedules      = $after_current_day + $before_current_day;

			$_next_time = [];
			foreach ( $new_schedules as $day_id => $schedules ) {
				if ( ! is_array( $schedules ) && empty( $schedules ) ) {
					continue;
				}

				$key = array_key_first( $schedules );

				if ( empty( $schedules[ $key ]['open'] ) && empty( $schedules[ $key ]['close'] ) ) {
					continue;
				}

				if ( isset( $schedules[ $key ]['open'] ) && isset( $schedules[ $key ]['close'] ) ) {
					$_next_time[ $day_id ] = $schedules;
					break;
				}
			}

			$day_id    = array_key_first( $_next_time ) ?: '';
			$schedules = $_next_time[ $day_id ];
			$times     = $this->calculate_times( $schedules, $time_for, $this->get_day_name( $day_id ) );
		}

		$next_time = reset( $times );

		if ( ! $return_utc ) {
			$next_time = apply_filters( 'woc_filters_next_time', wp_date( $format, $next_time, wp_timezone() ), $next_time, $format );
		} else {
			$next_time = apply_filters( 'woc_filters_next_time_utc', $next_time, $format );
		}

		return $next_time;
	}


	/**
	 * Calculate times
	 *
	 * @param array $__schedules
	 * @param string $__time_for
	 * @param string $__day_name
	 *
	 * @return mixed|void
	 */
	function calculate_times( $__schedules = array(), $__time_for = '', $__day_name = '' ) {

		$__times    = array();
		$__time_for = empty( $__time_for ) && ! in_array( $__time_for, array(
			'open',
			'close',
			'toggle',
		) ) ? 'toggle' : $__time_for;
		$__time_for = $__time_for == 'toggle' && wooopenclose()->is_open() ? 'close' : 'open';

		$__current_time = wp_date( 'U' );
		$__day_name     = empty( $__day_name ) ? $this->get_day_name() : $__day_name;
		$__day_name     = substr( $__day_name, 0, 3 );

		foreach ( $__schedules as $__schedule_id => $__schedule ) {
			if ( empty( $__schedule[ $__time_for ] ) ) {
				continue;
			}

			$this_time   = new DateTime( $__day_name . ' ' . $__schedule[ $__time_for ], wp_timezone() );
			$__this_time = $this_time->getTimestamp();

			if ( isset( $__schedule[ $__time_for ] ) && ! empty( $__this_time ) && $__current_time < $__this_time ) {
				$__times[] = $__this_time;
			}
		}

		return apply_filters( 'woc_filters_calculate_times', $__times, $__time_for, $__day_name );
	}


	/**
	 * Return Current Day Name
	 *
	 * @param string $day_id
	 * @param bool $return_label
	 *
	 * @return mixed|void
	 */
	function get_day_name( $day_id = '', $return_label = false ) {

		$day_id = empty( $day_id ) ? $this->get_current_day_id() : $day_id;
		$day    = isset( $this->get_days()[ $day_id ] ) ? $this->get_days()[ $day_id ] : array();

		if ( $return_label ) {
			$day_name = isset( $day['label'] ) ? $day['label'] : __( 'Not Found!', 'woc-open-close' );
		} else {
			$day_name = isset( $day['name'] ) ? $day['name'] : __( 'Not Found!', 'woc-open-close' );
		}

		return apply_filters( 'woc_filters_day_name', $day_name, $day_id );
	}


	/**
	 * Return all days
	 *
	 * @return mixed|void
	 */
	public function get_days() {

		$woc_start_of_week = Utils::get_option( 'woc_start_of_week', 2 );
		$days_array        = array(
			'10001' => array(
				'name'  => esc_html( 'Saturday' ),
				'label' => __( 'Saturday', 'woc-open-close' ),
			),
			'10002' => array(
				'name'  => esc_html( 'Sunday' ),
				'label' => __( 'Sunday', 'woc-open-close' ),
			),
			'10003' => array(
				'name'  => esc_html( 'Monday' ),
				'label' => __( 'Monday', 'woc-open-close' ),
			),
			'10004' => array(
				'name'  => esc_html( 'Tuesday' ),
				'label' => __( 'Tuesday', 'woc-open-close' ),
			),
			'10005' => array(
				'name'  => esc_html( 'Wednesday' ),
				'label' => __( 'Wednesday', 'woc-open-close' ),
			),
			'10006' => array(
				'name'  => esc_html( 'Thursday' ),
				'label' => __( 'Thursday', 'woc-open-close' ),
			),
			'10007' => array(
				'name'  => esc_html( 'Friday' ),
				'label' => __( 'Friday', 'woc-open-close' ),
			),
		);
		$sorted_days       = array();

		for ( $index = $woc_start_of_week; $index < ( $woc_start_of_week + 7 ); $index ++ ) {
			$day_id                 = ( $index % 7 === 0 ? 7 : $index % 7 ) + 10000;
			$sorted_days[ $day_id ] = $days_array[ $day_id ];
		}

		return apply_filters( 'woc_filters_days_array', $sorted_days );
	}


	/**
	 * Return all Schedules
	 *
	 * @param string $set_id
	 *
	 * @return mixed|void
	 */
	function get_all_schedules( $set_id = '' ) {

		$all_schedules = array();

		if ( empty( $this->get_active_schedule_id() ) ) {
			return $all_schedules;
		}

		$set_id            = empty( $set_id ) ? $this->get_active_schedule_id() : $set_id;
		$wooopenclose_data = Utils::get_meta( 'wooopenclose_data', $set_id );

		foreach ( $this->get_days() as $day_id => $label ) {
			$all_schedules[ $day_id ] = Utils::get_args_option( $day_id . '_schedules', $wooopenclose_data );
		}

		return apply_filters( 'woc_all_schedules', $all_schedules );
	}


	/**
	 * Return Plugin Path
	 *
	 * @return mixed|void
	 */
	function plugin_path() {
		return apply_filters( 'woc_filters_plugin_path', untrailingslashit( WOOOPENCLOSE_PLUGIN_DIR ) );
	}


	/**
	 * Return Message if Shop is Closed
	 *
	 * @return mixed|string|void
	 */
	function get_message() {

		$default_message = esc_html__( 'Offline ! We will start taking orders in %countdown%', 'woc-open-close' );
		$woc_message     = wooopenclose_get_schedule_meta( 'message', $default_message );

		// Handle instant controls
		if ( woc_pro_available() ) {
			if ( Utils::get_option( 'woc_instant_controls', false ) ) {
				$woc_message = Utils::get_option( 'woc_instant_force_msg',
					esc_html__( 'We are completely off till next update', 'woc-open-close' )
				);
			}
		}

		// Handle resplacer
		if ( strpos( $woc_message, '%countdown' ) !== false ) {

			$matches     = array();
			$woc_message = str_replace( '%countdown%', '%countdown-1%', $woc_message );

			preg_match( '/countdown-([0-9]+)?/', $woc_message, $matches );

			$cd_style    = isset( $matches[1] ) ? $matches[1] : 1;
			$cd_style    = get_query_var( 'in_status_bar' ) ? 1 : $cd_style;
			$woc_message = preg_replace( '/%countdown-([0-9]+)%?/', $this->get_countdown_timer( $cd_style ), $woc_message );
		}

		return apply_filters( 'woc_filters_shop_close_message', $woc_message );
	}


	/**
	 * Return countdown timer code
	 *
	 * @param $style
	 *
	 * @return string
	 */
	function get_countdown_timer( $style = 1 ) {
		return do_shortcode( sprintf( '[woc_countdown_timer style="%s"]', $style ) );
	}


	/**
	 * Return Button Text on Bar
	 *
	 * @return mixed|void
	 */
	function get_bar_btn_text() {

		return apply_filters( 'woc_filters_bar_btn_text', Utils::get_option( 'woc_bar_hide_text', esc_html__( 'Hide Message', 'woc-open-close' ) ) );
	}


	/**
	 * Return Whether Bar button to display or not
	 *
	 * @return bool
	 */
	function is_display_bar_btn() {
		return Utils::get_option( 'woc_bar_btn_display', true );
	}


	/**
	 * Print notice to the admin bar
	 *
	 * @param string $message
	 * @param bool $is_success
	 * @param bool $is_dismissible
	 */
	function print_notice( $message = '', $is_success = true, $is_dismissible = true, $is_frontend = false ) {

		if ( empty ( $message ) ) {
			return;
		}

		if ( is_bool( $is_success ) ) {
			$is_success = $is_success ? 'success' : 'error';
		}


		if ( $is_frontend ) {
			printf( '<div class="woocommerce-notices-wrapper"><div class="woocommerce-message %s" role="alert">%s</div></div>', esc_attr( $is_success ), esc_html( $message ) );

			return;
		}

		printf( '<div class="notice notice-%s %s">%s</div>', esc_attr( $is_success ), esc_attr( $is_dismissible ? 'is-dismissible' : '' ), $message );
	}


	/**
	 * Generate and return HTML for Schedule
	 *
	 * @param array $schedule
	 */
	function generate_woc_schedule( $schedule = array() ) {

		woc_update_global_arguments( $schedule );

		woc_get_template( 'admin/single-schedule.php', $schedule );
	}


	/**
	 * Return current URL with HTTP parameters
	 *
	 * @param $http_args
	 * @param $wp_request
	 *
	 * @return mixed|void
	 */
	function get_current_url( $http_args = array(), $wp_request = '' ) {

		global $wp;

		$current_url = empty( $wp_request ) ? site_url( $wp->request ) : $wp_request;
		$get_args    = array_map( 'sanitize_text_field', $_GET );
		$http_args   = array_merge( $get_args, $http_args );

		if ( ! empty( $http_args ) ) {
			$current_url .= '?' . http_build_query( $http_args );
		}

		return apply_filters( 'woc_filters_current_url', $current_url );
	}


	function get_option( $option_key, $default = '' ) {
		return Utils::get_option( $option_key, $default );
	}
}

global $wooopenclose;
$wooopenclose = new WOOOPENCLOSE_Functions();